Bash(cd /c/Users/rdzingel/Documents/MY_APPS/HARMONIC_WAVES/tideresonance.app && node -e "
const { createTidePredictor } = require\('@neaps/tide-predictor'\);
const { nearest, search, stations } = require\('@neaps/tide-database'\);

// Test 1: Find nearest station to Whitby
console.log\('=== TEST 1: Find Nearest Station to Whitby \(54.486, -0.615\) ==='\);
const result = nearest\({ latitude: 54.486, longitude: -0.615 }\);
if \(!result\) { console.log\('No station found!'\); process.exit\(1\); }
const [station, dist] = result;
console.log\('Station:', station.name\);
console.log\('ID:', station.id\);
console.log\('Type:', station.type\);
console.log\('Distance:', dist.toFixed\(1\), 'km'\);
console.log\('Coords:', station.latitude, station.longitude\);
console.log\('Country:', station.country\);
console.log\('Timezone:', station.timezone\);
console.log\(''\);

// Test 2: Get predictions
console.log\('=== TEST 2: Tide Predictions ==='\);
let constituents, offsets;
if \(station.type === 'reference'\) {
  constituents = station.harmonic_constituents;
} else if \(station.offsets\) {
  const ref = stations.find\(s => s.id === station.offsets.reference\);
  console.log\('Reference station:', ref.name, ref.id\);
  constituents = ref.harmonic_constituents;
  offsets = station.offsets;
}

const predictor = createTidePredictor\(constituents\);

const now = new Date\(\);
const start = new Date\(now\); start.setDate\(start.getDate\(\) - 1\); start.setHours\(0,0,0,0\);
const end = new Date\(now\); end.setDate\(end.getDate\(\) + 1\); end.setHours\(23,59,59,999\);

const extremes = predictor.getExtremesPrediction\({
  start, end,
  labels: { high: 'High', low: 'Low' },
  ...\(offsets ? { offsets: { height: offsets.height, time: offsets.time } } : {}\)
}\);

console.log\('Extremes \(' + extremes.length + ' total\):'\);
extremes.forEach\(e => {
  const t = e.time;
  const dateStr = t.toLocaleDateString\('en-GB', { weekday: 'short', day: 'numeric', month: 'short' }\);
  const timeStr = t.toLocaleTimeString\('en-GB', { hour: '2-digit', minute: '2-digit' }\);
  console.log\('  ' + e.label + ': ' + e.level.toFixed\(2\) + 'm @ ' + dateStr + ' ' + timeStr\);
}\);

// Test 3: Current height
const current = predictor.getWaterLevelAtTime\({ time: now }\);
console.log\(''\);
console.log\('Current height at', now.toLocaleTimeString\('en-GB'\) + ':', current.level.toFixed\(3\), 'm'\);

// Test 4: Timeline
const tlStart = new Date\(now\); tlStart.setHours\(0,0,0,0\);
const tlEnd = new Date\(now\); tlEnd.setHours\(23,59,59,999\);
const timeline = predictor.getTimelinePrediction\({ start: tlStart, end: tlEnd, timeFidelity: 600 }\);
console.log\('Timeline points:', timeline.length\);
console.log\(''\);

// Test 5: Station search
console.log\('=== TEST 3: Station Search ==='\);
const liverpool = search\('Liverpool'\);
console.log\('Liverpool:', liverpool.slice\(0,3\).map\(s => s.name + ' \(' + s.country + '\)'\)\);
const london = search\('London'\);
console.log\('London:', london.slice\(0,3\).map\(s => s.name + ' \(' + s.country + '\)'\)\);
const sanfran = search\('San Francisco'\);
console.log\('San Francisco:', sanfran.slice\(0,3\).map\(s => s.name + ' \(' + s.country + '\)'\)\);

// Test 6: UK coordinates nearest
console.log\(''\);
console.log\('=== TEST 4: Nearest to Central London \(51.507, -0.127\) ==='\);
const londonNearest = nearest\({ latitude: 51.507, longitude: -0.127 }\);
if \(londonNearest\) {
  console.log\('Station:', londonNearest[0].name, '\(' + londonNearest[1].toFixed\(1\) + ' km\)'\);
}

console.log\(''\);
console.log\('Total stations in database:', stations.length\);
" 2>&1)
